sys_ui_script.script:
(function () {
    'use strict';

    angular
        .module('twc-product')
        .factory('UserFactory', UserFactory);

    UserFactory.$inject = ['$resource'];

    function UserFactory($resource) {
		return $resource('', {}, {
			profile: {method: 'GET', url: '/api/x_wec_product/table/sys_user/:id'},
			update: {method: 'PATCH', url: '/api/x_wec_product/table/sys_user/:id'},
			search: {method: 'GET', url: '/api/x_wec_product/table/sys_user'},
			userHasRole: {method: 'GET', url: '/api/x_wec_product/table/sys_user_has_role'},
			fullUsers: {
				method: 'GET', 
				url: '/api/x_wec_product/table/sys_user',
				transformResponse: function(data, headers){
					var response = {}
					response.data = angular.fromJson(data);
					response.headers = headers();
					return response;
				}
			},			

		})

    }

})();
